import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import moment from 'moment';

const Detail = () => {
    const { id } = useParams();
    const [news, setNews] = useState(null);
    const [comment, setComment] = useState('');

    useEffect(() => {
        axios.get(`http://127.0.0.1:8000/api/v1/ads/${id}/`)
            .then(response => {
                const data = response.data;
                setNews(data);
            })
            .catch(error => {
                console.error(error);
            });
    }, [id]);

    const handleCommentChange = (e) => {
        setComment(e.target.value);
    };

    const handleSubmitComment = (e) => {
        e.preventDefault();
        // Send the comment to the server or perform other necessary actions
        console.log(comment);
        setComment('');
    };

    if (news === null) {
        return <h2>Loading...</h2>;
    }

    const formattedDate = moment(news.created_at).format("D MMMM YYYY");

    return (
        <div>
            <div className="container-detail">
                <h1>{news.title}</h1>
                <img className='img-detail' src={`http://localhost:8000${news.img}`} alt="Ad Image"></img>
                <div className='div-detail'>
                    <p className='p-detail'>Просмотров: {news.count_views}</p>
                    <p className='p-detail'>Город: {news.city}</p>
                </div>
                <p>{news.descriptions}</p>
                <p className="price">Цена: {news.price} рублей</p>
                <p className="telephone">Телефон: {news.telephone}</p>
                <p className="date">Дата публикации: {formattedDate}</p>

                <h3>Leave a Comment</h3>
                <form onSubmit={handleSubmitComment}>
                    <textarea
                        value={comment}
                        onChange={handleCommentChange}
                        placeholder="Enter your comment"
                        rows="4"
                    ></textarea>
                    <button type="submit">Submit</button>
                </form>

                {/* Add styling for the comment form */}
                <style jsx>{`
                    .container-detail {
                        /* your existing styles */
                    }

                    .img-detail {
                        /* your existing styles */
                    }

                    .div-detail {
                        /* your existing styles */
                    }

                    .p-detail {
                        /* your existing styles */
                    }

                    .price {
                        /* your existing styles */
                    }

                    .telephone {
                        /* your existing styles */
                    }

                    .date {
                        /* your existing styles */
                    }

                    textarea {
                        /* your comment form styles */
                    }

                    button {
                        /* your comment form button styles */
                    }
                `}</style>
            </div>
        </div>
    );
};

export default Detail;










