import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import moment from 'moment';

const Detail = () => {
    const { id } = useParams();
    const [news, setNews] = useState(null);
    const [comment, setComment] = useState('');
    const [success, setSuccess] = useState(false);
    const token = localStorage.getItem('token'); // Получение токена из localStorage или другого места

    useEffect(() => {
        axios.get(`http://127.0.0.1:8000/api/v1/ads/${id}/`)
            .then(response => {
                const data = response.data;
                setNews(data);
            })
            .catch(error => {
                console.error(error);
            });
    }, [id]);

    const handleCommentChange = (event) => {
        setComment(event.target.value);
    };

    const handleSubmit = (event) => {
        event.preventDefault();

        axios.post(`http://127.0.0.1:8000/api/v1/ads/${id}/comments/`, { comment }, {
            headers: {
                Authorization: `Bearer ${token}` // Заголовок токена авторизации
            }
        })
            .then(response => {
                setComment('');
                setSuccess(true);
            })
            .catch(error => {
                console.error(error);
            });
    };

    if (news === null) {
        return <h2>Loading...</h2>;
    }

    const formattedDate = moment(news.created_at).format("D MMMM YYYY");

    return (
        <div>
            <div className="container-detail">
                <h1>{news.title}</h1>
                <img className='img-detail' src={`http://localhost:8000${news.img}`} alt="Ad Image" />
                <div className='div-detail'>
                    <p className='p-detail'>Просмотров: {news.count_views}</p>
                    <p className='p-detail'>Город: {news.city}</p>
                </div>
                <p>{news.description}</p>
                <p className="price">Цена: {news.price} рублей</p>
                <p className="telephone">Телефон: {news.telephone}</p>
                <p className="date">Дата публикации: {formattedDate}</p>

                <form onSubmit={handleSubmit} className="comment-form">
                    <textarea
                        value={comment}
                        onChange={handleCommentChange}
                        placeholder="Оставьте комментарий"
                        required
                    ></textarea>
                    <button type="submit">Отправить комментарий</button>
                </form>

                {success && <p className="success-msg">Комментарий успешно отправлен!</p>}
            </div>
        </div>
    );
};

export default Detail;









