import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';

import Home from './Home';
import Login from './Login';
import Register from './Register';

function App() {
  const [isLoggedIn, setLoggedIn] = useState(false);

  useEffect(() => {
    const checkLoginStatus = async () => {
      const token = localStorage.getItem('access_token');

      if (token) {
        try {
          const response = await axios.get(
            'http://127.0.0.1:8000/api/v1/check_login/',
            {
              headers: {
                Authorization: `Token ${token}`,
              },
            }
          );
          setLoggedIn(response.data.authenticated);

        } catch (error) {
          console.error(error);
          // Обработка ошибки авторизации
        }
      }
    };

    checkLoginStatus();
  }, []);

  const handleLogout = () => {
    setLoggedIn(false);
    localStorage.removeItem('access_token');
    localStorage.removeItem('refresh_token');


  };



  return (
    <Router>
      <header>
        <div className='header'>

          <p className='logo'>Логотип</p>
          <div className='div-check'>

            <div>
              <Link className='active' to="/">Главная</Link>
            </div>
            {isLoggedIn ? (
              <div className='chek-auth'>
                <p className='divp-'>Вы вошли в систему.</p>
                <Link to="/" onClick={handleLogout}>
                  Выход
                </Link>
              </div>
            ) : (
              <div className='chek-auth'>
                <p className='p-div'>Вы не вошли в систему.</p>
                <Link to="/auth">Войти</Link>
              </div>
            )}
          </div>
        </div>
      </header>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/auth" element={<Login />} />
        <Route path="/register" element={<Register />} />
      </Routes>
    </Router>
  );
}

export default App;

