import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const Detail = () => {
    const { id } = useParams();
    const [news, setNews] = useState(null);

    useEffect(() => {
        axios.get(`http://127.0.0.1:8000/api/v1/ads/${id}/`)
            .then(response => {
                const data = response.data;
                setNews(data);
            })
            .catch(error => {
                console.error(error);
            });
    }, [id]);

    if (news === null) {
        return <h2>Loading...</h2>;
    }

    return (
        <div>
            <div class="container">
                <h1>{news.title}</h1>
                <img src={`http://localhost:8000${news.img}`} alt="Ad Image"></img>
                    <p></p>
                    <p class="price">Цена: 67777.00</p>
                    <p class="telephone">Телефон: +79490000000</p>
                    <p class="date">Дата публикации: 2023-10-07T22:24:31.644549+03:00</p>
            </div>


        </div>
    );
};

export default Detail;
